import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "./Navbar.scss";

const Navbar = () => {
  const [active, setActive] = useState(false);
  const [open, setOpen] = useState(false);

  const { pathname } = useLocation();

  const isActive = () => {
    window.scrollY > 0 ? setActive(true) : setActive(false);
  };

  useEffect(() => {
    window.addEventListener("scroll", isActive);

    return () => {
      window.removeEventListener("scroll", isActive);
    };
  }, []);

  const currentUser = {
    id: 1,
    username: "Rahul k",
    isSeller: true,
  };

  const menuItems = [
    {id:1, name: "Graphics & Design", to: "/" },
    {id:2, name: "Video & Animation", to: "/" },
    {id:3, name: "Writing & Translation", to: "/" },
    {id:4, name: "AI Services", to: "/" },
    {id:5, name: "Digital Marketing", to: "/" },
    {id:6, name: "Music & Audio", to: "/" },
    {id:7, name: "Programming & Tech", to: "/" },
    {id:8, name: "Business", to: "/" },
    {id:9, name: "Lifestyle", to: "/" },
  ];

  return (
    <div className={active || pathname !== "/" ? "navbar active" : "navbar"}>
      <div className="container">
        <div className="logo">
          <Link to="/" className="link">
            <span>fiverr</span>
          </Link>
          <span className="dot">.</span>
        </div>
        <div className="links">
          <Link className="link">Fiverr Bussiness</Link>
          <Link className="link">Explore</Link>
          <Link className="link">English</Link>
          {!currentUser?.isSeller && (
            <Link className="link">Become A Seller</Link>
          )}
          <Link className="link">Signin</Link>
          {!currentUser && (
            <button className={active ? "active-btn" : ""}>Join</button>
          )}
          {currentUser && (
            <div className="user" onClick={() => setOpen(!open)}>
              <img
                src="data:image/jpeg;base64,"
                alt=""
              />
              <span>{currentUser?.username}</span>
              {open && (
                <div className="options">
                  {currentUser?.isSeller && (
                    <>
                      <Link to="/mygigs" className="link">
                        Gigs
                      </Link>
                      <Link to="/add" className="link">
                        Add New Gig
                      </Link>
                    </>
                  )}
                  <Link to="/orders" className="link">
                    Orders
                  </Link>
                  <Link to="/messages" className="link">
                    Messages
                  </Link>
                  <Link className="link">Logout</Link>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
      {(active ||
        pathname !== "/") && (
          <>
            <hr />
            <div className="menu">
              {menuItems.map(item => <Link key={item.id} className="link" path={item.to}>{item.name}</Link>)}
            </div>
            <hr/>
          </>
        )}
    </div>
  );
};

export default Navbar;
